script.content:
$('#run').click(function() {
    invokeRun()
        .catch(OfficeHelpers.logError);
});

function invokeRun() {
    return Excel.run(function(context) {
        // Insert your code here. For example:
        var range = context.workbook.getSelectedRange();
        range.format.fill.color = "yellow";

        return context.sync();
    });
}

