document.querySelector('#run').addEventListener('click', function () {
    getData().catch(OfficeHelpers.Utilities.error);
});

function getData() {
    let url = 'https://jsonplaceholder.typicode.com';
    return fetch(`${url}/posts/1`)
        .then(res => res.json())
        .then(data => console.log(data));
}
